import { graphql } from 'react-apollo'
import CreateEmployee from './mutations/createEmployee'
import ListEmployees from './queries/ListEmployees'

class AddEmployee extends React.Component { 
    state = {
    id: '',
    firstName: '',
    lastName: '',
    skills: [],
  }

  onChange = (key, value) => {
    this.setState({ [key]: value })
  }

  addSkills = () => {
    if (this.state.skills === '') return
    const skills = this.state.skills
    skills.push(this.state.skilss)
    this.setState({
      skills,
      skills: ''
    })
  }  

  addEmployee = () => {
    const { firstName, lastName, skills } = this.state
        this.props.onAdd({
        firstName,
        lastName,
        skills
        })

        this.setState({
        firstName: '',
        lastName: '',
        skills: []
        })
    }

    EmployeeForm = () => {
    return (
        <FormControl className="forms">
            <TextField id="standard-basic" label="First Name" />
            <TextField id="standard-basic" label="Last Name" />
        </FormControl>
    )
}

    render(){
        const renderForm = this.state.visible ? ( <EmployeeForm /> ) : null;
        const buttonText = this.state.title ? 'Add Employee' : 'Submit'

        return (
            <div>
                {renderForm}
                <Button variant="contained" color="primary" className="addEmpButton"
                onClick={() => this.setState({visible: !this.state.visible, title: !this.state.title})} >
                    {buttonText}
                </Button>

                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Id</TableCell>
                            <TableCell>First Name</TableCell>
                            <TableCell>Last Name</TableCell>
                        </TableRow>
                    </TableHead>
                </Table>

            </div>
        )
    }
}

    export default graphql(CreateEmployee, {
    props: props => ({
        onAdd: employee => props.mutate({
        variables: employee,
        optimisticResponse: {
            __typename: 'Mutation',
            createEmployee: { ...employee,  __typename: 'Employee' }
        },
        update: (proxy, { data: { createEmployee } }) => {
            const data = proxy.readQuery({ query: ListEmployees });
            data.listEmployee.items.push(listEmployee);
            proxy.writeQuery({ query: ListRecipes, data });
        }
        })
    })
    })(AddEmployee)
