import LIST_EMPLOYEE from '../queries/ListEmployees';
import { useQuery } from '@apollo/client';
import { Query } from "react-apollo";
import { ApolloProvider } from 'react-apollo';
import Loading from 'react-loading';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

const ListOfEmployees = () => {

    const shortID = (id) => {
        var str = id;
        var sID = str.substring(0, 7);
        return sID;

    }

    return (
        <Query query={LIST_EMPLOYEE}>
        {({ loading, error, data }) => {
          if (loading) return <Loading loading={loading} />;
          if (error) console.log(error);
          return (
            // <p>
            //     {console.log(data.listEmployees.items)}
            // </p>

        <div>
            {data.listEmployees.items.map(row => (
                   <TableRow>
                       <TableCell component="th" scope="row" >{shortID(row.id)}</TableCell>
                        <TableCell style={{ padding: "50px" }}>{row.firstname}</TableCell>
                        <TableCell>{row.lastname}</TableCell>
                        <TableCell>{row.skills}</TableCell>
                   </TableRow>
                ))
            }   
         </div>
            )}
        }
      </Query>
    )
}

export default ListOfEmployees;
