import { Query } from 'react-apollo'
import React, { useState } from 'react';
import { Mutation } from 'react-apollo'
import Table from '@material-ui/core/Table';    
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import { FormControl, TableBody } from '@material-ui/core';
import { TextField } from '@material-ui/core';
import { useMutation, useQuery } from '@apollo/client';
import CREATE_EMPLOYEE from './mutations/createEmployee'
import LIST_EMPLOYEE from './queries/ListEmployees'

import './index.css';

const ListOfEmployees = () => {
    const { loading, error, data } = useQuery(LIST_EMPLOYEE);

    if (loading) return 'Loading...';
    if (error) return `Error! ${error.message}`;

    return (
        //<TableBody>
        <p>
            {data.listEmployees.items.map(row => (

                    <p>
                    {console.log(row)}
                    </p> 
            ))}
        </p>

                // <TableRow key={row.id}>
                //     <TableCell component="th" scope="row">
                //         {row.id}
                //         </TableCell>
                //          <TableCell align>{row.id}</TableCell>
                //         <TableCell align>{row.firstname}</TableCell>
                //         <TableCell align>{row.lastname}</TableCell>
                //         {/* <TableCell align onClick={() => this.editUser(row.id)}>Edit</TableCell>
                //         <TableCell align onClick={() => this.deleteUser(row.id)}>Delete</TableCell> */}
                // </TableRow>    
        //</TableBody>
    )
}

const EmployeeForm = () => {

    const [visible, setVisible] = useState(false);
    const [title, setTitle] = useState('Add Emmployee');
    const [id, setID] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [skills, setSkills] = useState([]);

    const renderForm = visible ? ( <EmployeeForm /> ) : null;
    const buttonText = title ? 'Add Employee' : setTitle('Back');

    const clearForm = () => {
        setFirstName('');
        setLastName('');
    }

    const submitButtonClicked = () => {
        if (visible) {
            console.log(firstName)
            console.log(lastName)
        }
    }


    return (
        <FormControl className="forms">
            <TextField id="standard-basic" label="First Name" value={setFirstName} onChange={evt => this.onChange('firstName', evt.target.value)} />
            <TextField id="standard-basic" label="Last Name" value={setLastName} onChange={evt => this.onChange('lastName', evt.target.value)}/>
            <Mutation mutation={CREATE_EMPLOYEE} variables={{firstname: firstName, lastname: lastName}}>
                {postMutation => <Button variant="contained" color="primary" 
                    onClick={() => {
                        postMutation();
                        submitButtonClicked();
                        clearForm();
                    }}>
                Submit
                </Button>}
            </Mutation>
        </FormControl>
    )
}

const AddEmployee = () => { 

    return (
        <div>
            {renderForm}
            <Button variant="contained" color="primary" className="addEmpButton"
            onClick={() => setVisible(true), setTitle('Back')} 
            >
                {buttonText}
            </Button>

            <Table>
                <TableHead>
                    <TableRow>
                        <TableCell>Id</TableCell>
                        <TableCell>First Name</TableCell>
                        <TableCell>Last Name</TableCell>
                    </TableRow>
                </TableHead>
            </Table>

            {/* <Query query={LIST_EMPLOYEE}>

            </Query> */}
        </div>
    )
}

export default AddEmployee;
