import { graphql } from 'react-apollo'
import React, { Component } from 'react'
import EmployeeForm from './Components/employeeForm'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import { FormControl } from '@material-ui/core';
import { TextField } from '@material-ui/core';

import './index.css';

class AddEmployee extends React.Component { 
    state = {
    id: '',
    firstName: '',
    lastName: '',
    skills: [],
    visible: false,
    title: ''
  }

  onChange = (key, value) => {
    this.setState({ [key]: value })
  }

  addSkills = () => {
    if (this.state.skills === '') return
    const skills = this.state.skills
    skills.push(this.state.skilss)
    this.setState({
      skills,
      skills: ''
    })
  }  

  addEmployee = () => {
    const { firstName, lastName, skills } = this.state
        this.props.onAdd({
        firstName,
        lastName,
        skills
        })

        this.setState({
        firstName: '',
        lastName: '',
        skills: []
        })
    }

    EmployeeForm = () => {
    return (
        <FormControl className="forms">
            <TextField id="standard-basic" label="First Name" />
            <TextField id="standard-basic" label="Last Name" />
        </FormControl>
    )
}

    render(){
        const renderForm = this.state.visible ? ( <EmployeeForm /> ) : null;
        const buttonText = this.state.title ? 'Add Employee' : 'Submit'

        return (
            <div>
                {renderForm}
                <Button variant="contained" color="primary" className="addEmpButton"
                onClick={() => this.setState({visible: !this.state.visible, title: !this.state.title})} >
                    {buttonText}
                </Button>

                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Id</TableCell>
                            <TableCell>First Name</TableCell>
                            <TableCell>Last Name</TableCell>
                        </TableRow>
                    </TableHead>
                </Table>

            </div>
        )
    }
}

