import gql from 'graphql-tag'

const CreateEmployee = gql` 
  mutation createEmployee (
      $firstName: String!,
      $lastName: String!,
    ) {
    createEmployee(input: $input, condition: $condition){
        id
      firstName
      lastName
      skills
    }
}
`
export default CreateEmployee;
